<?php $sFeed = $_GET["feed"];// All Rss feeds are expected to return XML:header("Content-Type: text/xml");//Hard-code list of rss resources that are accessible via this script:$aFeeds = array();$aFeeds["yuiblog"] = array("url"=>"http://feeds.yuiblog.com/YahooUserInterfaceBlog");$aFeeds["ydn-javascript"] = array("url"=>"http://rss.groups.yahoo.com/group/ydn-javascript/rss");// Open the Curl session$session = curl_init($aFeeds[$sFeed]["url"].'?rnd='.rand(100000,1000000));// Don't return HTTP headers. // Do return the contents of the callcurl_setopt($session, CURLOPT_HEADER, false);curl_setopt($session, CURLOPT_RETURNTRANSFER, true);// Make the call$results = curl_exec($session);echo $results;curl_close($session);?>